import java.time.LocalDateTime
import java.time.format.DateTimeFormatter
import java.time.temporal.WeekFields
import java.util.Locale

// This function is invoked for each record
def transform(record, context) {
    // Read timestamp string
    def ts = record.getAsString("timestamp")
    Integer weekOfYear = null

    if (ts) {
        try {
            // Use formatter that matches your timestamp format: "2022-04-11 12:47:27"
            def formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
            def dt = LocalDateTime.parse(ts, formatter)
            def weekFields = WeekFields.ISO
            weekOfYear = dt.get(weekFields.weekOfWeekBasedYear())

            // Optional: Log success for debugging
            context.logger.info("Successfully parsed timestamp: ${ts} -> week ${weekOfYear}")

        } catch (Exception ex) {
            // parsing failed, leave weekOfYear as null
            context.logger.warn("Failed to parse timestamp: ${ts}, error: ${ex.message}")
        }
    } else {
        context.logger.warn("Timestamp field is null or empty")
    }

    // Add the new field for *every* record
    record.setValue("week_of_year", weekOfYear)

    return record
}
